package net.java.efurture.reader.image;

import java.awt.Graphics2D;
import java.awt.RenderingHints;
import java.awt.color.CMMException;
import java.awt.image.BufferedImage;
import java.io.ByteArrayOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.net.URL;
import java.util.Map;

import javax.imageio.ImageIO;

import org.apache.commons.codec.binary.Base64;
import org.apache.commons.io.IOUtils;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.util.StringUtils;

public class ImageUtils {

	protected static Logger logger = LoggerFactory.getLogger(ImageUtils.class);
	
	public static final int MAX_WIDTH = 640;
	
	public static final int MAX_HEIGHT = MAX_WIDTH*2;
	/**
	 * base64 image
	 * 
	 * 
     * <img src="data:image/jpg;base64,">
	 * */
	public static String base64ImageFromUrl(String url){
		try {
			if(url == null){
				logger.warn("base64Image image null, url is empty");
				return null;
			}
			
			Map<String,Object> map = ImageClient.grapImage(url);
			if(map == null || map.get(ImageClient.KEY_INPUT) == null){
				logger.warn("base64Image image null, none input stream");
				return null;
			}
			
			String mimeType = (String) map.get("extension");
			if(StringUtils.isEmpty(mimeType)){
				if(map.get(ImageClient.KEY_INPUT) != null){ //如果未取到，则采用JPG为默认格式
					logger.warn("mimeType not found " + url + " use default jpg");
					mimeType = "jpg";
				}
			}
			InputStream in = (InputStream) map.get("input");
			try{
				BufferedImage image = ImageIO.read(in);  //可以不用读取bufferimage，直接将inputstream转码
				if(image == null){
					logger.error("read image null, direct base64 image " + url);
					return base64ImageFromInput(in, mimeType);
				}
				String base64  = null;
				if(image.getWidth() <= MAX_WIDTH){
					if(image.getHeight() > MAX_HEIGHT){
						image = image.getSubimage(0, 0, image.getWidth(), MAX_HEIGHT);
					}
					base64 =  base64ImageFromBufferImage(image, mimeType);
					image = null;
					return base64;
				}
				
				int height = (int)(image.getHeight()*MAX_WIDTH/(1.0*image.getWidth()));
				height = Math.min(height, MAX_HEIGHT);
				BufferedImage scaledImage = new BufferedImage(MAX_WIDTH, height, image.getType());
				Graphics2D g2d = (Graphics2D)scaledImage.createGraphics();
				g2d.addRenderingHints(new RenderingHints(RenderingHints.KEY_RENDERING,
	                                RenderingHints.VALUE_RENDER_QUALITY));
				g2d.addRenderingHints(new RenderingHints(RenderingHints.KEY_INTERPOLATION,
					    RenderingHints.VALUE_INTERPOLATION_BICUBIC));
				
				
				g2d.drawImage(image, 0, 0, MAX_WIDTH, height, null);
				g2d.dispose();
				base64 =  base64ImageFromBufferImage(scaledImage, mimeType);
				image = null;
				scaledImage = null; //释放内存
				return base64;
			}catch(Exception e){ //某些图片解码失败，直接读取其内容进行转换。
				if(e instanceof CMMException){
					logger.error("read image CMMException, direct base64 image" + url);
				}else{
					logger.error("read image Exception, direct base64 image" + url , e);
				}
				return base64ImageFromInput(in, mimeType);
			}finally{
				IOUtils.closeQuietly(in);
			}
			
		} catch (Exception e) {
			logger.error("base64 image exception " + url, e);
		}
		return null;
	}
	
	public static String base64ImageFromUrl(URL url){
		return base64ImageFromUrl(url.toString());
	}
	
	
	/**
	 * 将图片转换为base64编码格式
	 * */
	public static String base64ImageFromInput(InputStream in, String mimeType) throws IOException{
		in.reset(); //复位，重头开始读
		byte[]  bts = IOUtils.toByteArray(in);
		String base64 = Base64.encodeBase64String(bts);
		return "data:image/"+ mimeType +";base64," + base64;
	}
	
	public static String base64ImageFromBufferImage(BufferedImage image, String mimeType){
		try{
			ByteArrayOutputStream output = new ByteArrayOutputStream(1024*8);
			ImageIO.write(image, mimeType, output);
			String base64 = Base64.encodeBase64String(output.toByteArray());
			return "data:image/"+ mimeType +";base64," + base64;
		}catch(Exception e){
		   e.printStackTrace();
		   return null;
		}
	}
	
}
